
        $(document).ready(function(){
            
            $('#branch').on('submit', function(e) {
                
                e.preventDefault(); // prevent native submit

                // getting Url From Select Box
                url = $('#url').val()
                
                //alert(url)
                // Change the target 
                $(this).attr('action',url)

                $('#DataTable tbody').html('')
                
                $(this).ajaxSubmit({
                    beforeSubmit:  showRequest, 
                    success:       showResponse 
                })

            }); 
        });
        
        // Befor Submitting the form
        function showRequest(formData, jqForm, options){  
            var queryString = $.param(formData); 
            //alert('About to submit: \n\n' + queryString); 
            return true; 
        }

        // After Submitting the form
        function showResponse(data, statusText, xhr, $form)  {  
            
            
            console.log(data)
            cat_code = '0'
            $.each(data,function(i,v){
               
               
               
               if(v.cat_code!=cat_code){
                    row =  $('<tr class="cat_row">')
                    d1 = $('<td colspan="3" class="cat_name">').html('<h5><ion-icon name="albums-outline"></ion-icon> '+v.cat_desc+'</h5>')                    
                    row.append(d1)                   
                    $('#DataTable tbody').append(row)
                    cat_code=v.cat_code
               }
               
               row =  $('<tr>')
               d1 = $('<td>').html(v.desc)
               d2 = $('<td>').html(v.qty)
               d3 = $('<td>').html('---')
               row.append(d1)
               row.append(d2)
               row.append(d3)
               $('#DataTable tbody').append(row)
            })

        } 


        $('.export').click(function(e){
            
            e.preventDefault()

            

            //$("#DataTable").tableToCSV();
            $("#DataTable").table2excel({

                //exclude:".noExl",

                name: $('#url option:selected').text()+"-"+$('#date').val(),

                filename:$('#url option:selected').text()+"-"+$('#date').val(),//do not include extension

                fileext:".xlsx" // file extension

            });




        })
